    mrktCtg, clpr, vs, fltRt, mkp,
    hipr, lopr, trqu, trPrc, istgStCnt, mrktTotAmt
) VALUES (
             #{stockCode}, #{stockName}, #{dailyPrice}, #{field},
             #{mrktCtg}, #{clpr}, #{vs}, #{fltRt}, #{mkp},
             #{hipr}, #{lopr}, #{trqu}, #{trPrc}, #{istgStCnt}, #{mrktTotAmt}
         )
